import { mapGetters, mapActions } from 'vuex';

export default {
  data() {
    return {
      search: '',
      selected: [],
      headers: [
        {
          text: 'Fullname',
          align: 'center',
          value: 'fullname',
          width: '12%',
        },
        {
          text: 'Birthday',
          value: 'birthday',
          width: '10%',
          sortable: false,
          align: 'center',
        },
        {
          text: 'Image',
          value: 'imageFile',
          width: '15%',
          sortable: false,
          align: 'center',
        },
        {
          text: 'Followers',
          value: 'followers',
          width: '2%',
          sortable: false,
          align: 'center',
        },
        {
          text: 'Country',
          value: 'country',
          class: 'break-long-text',
          width: '6%',
          sortable: false,
          align: 'center',
        },
        { text: 'Song', value: 'song', sortable: false },
        {
          text: 'Biography',
          value: 'biography',
          width: '20%',
          sortable: false,
          align: 'center',
        },
        {
          text: 'action',
          sortable: false,
          value: 'action',
          align: 'center',
        },
      ],
      singers: [
        {
          id: 1,
          fullname: 'dang tuan phong',
          birthday: '13/11/1998',
          imageFile:
            'https://image.shutterstock.com/image-photo/beautiful-water-drop-on-dandelion-260nw-789676552.jpg',
          followers: 20,
          country: 'Hung Yen',
          song: ['hay trao cho anh', 'em cua ngay hom qua'],
          biography:
            'fasfasfasfddddddddddddddfdsfsdfsddsfsdfsdddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddd',
        },
      ],
      drawer: true,
    };
  },
  created() {
    const loader = this.$loading.show();
    // console.log(window.location.pathname);
    // console.log(typeof(window.location.pathname))
    this.getSingerList()
      .then(() => {
        loader.hide();
      })
      .catch((error) => {
        this.$store.commit('setSnack', { snack: `Something went wrong, please try again later${error.message}`, color: 'error' });
        loader.hide();
      });
    this.$store.watch(
      state => state.hambuger.drawer,
      () => {
        const msg = this.$store.state.hambuger.drawer;
        if (msg !== '') {
          this.drawer = msg;
        }
      },
    );
  },
  computed: {
    ...mapGetters('singer', {
      singers: 'renderSingerList',
    }),
  },
  methods: {
    ...mapActions('singer', {
      getSingerList: 'getSingerList',
    }),
    deleteItem(item) {
      const message = 'Are you sure you want to delete this singer?';
      this.$store.commit('showDialog', {
        message,
        submitFunction: 'removeUser',
        data: item,
      });
    },
    edit(item) {
      this.$router.push({
        name: 'SingerEditPage',
        params: { id: item.id },
      });
    },
  },
};
